let online = function(){
    let events = this.events;
    if (events && events.status)
        return events.status.pld.online;
    return 0;
}

let cachePayload = function(payload) {
    let hd = payload.hd;
    let pld = payload.pld;
    if (hd.from.type == "dev") {
        let dev = data.devices[hd.from.id] || {events:{}};
        dev.id = hd.from.id;
        dev.events = dev.events || {};
        if (hd.from.pid)
            dev.pid = hd.from.pid;
        dev.events[hd.entry.id] = payload;
        dev.online = online;
        data.devices[hd.from.id] = dev;
        let edgId = hd.ids && hd.ids.edg_id || pld.edg_id
        if (edgId) {
            let edg = data.edges[edgId];
            if (edg) {
                dev.edge = edg;
                edg.devices[dev.id] = dev;;
            }
        }
    }

    if (hd.from.type == "edg") {
        let edg = data.edges[hd.from.id] || {};
        edg.events = edg.events || {};
        edg.id = hd.from.id;
        edg.events[hd.entry.id] = payload;
        edg.online = online;
        data.edges[hd.from.id] = edg;
    }
}

common.cachePayload = cachePayload;

