let getHDTopic = function(hd) {
    let tpc = "";
    let ids = hd.ids || global.get("data").ids;
    if (!hd.from) hd.from = {};
    if (!hd.from.type) hd.from.type = "dio";
    if (!hd.from.id) hd.from.id = ids.dio;
    if (!hd.to) hd.to = {};
    if (!hd.to.type) hd.to.type = "0";
    if (!hd.to.id) hd.to.id = "0";
    if (!hd.entry) hd.entry = {};
    if (!hd.entry.type) hd.entry.type = "0";
    if (!hd.entry.id) hd.entry.id = "0";
    if (!hd.sid) hd.sid = "0";
    if (!hd.stp) hd.stp = 0;


    tpc =   (ids.app ? ids.app : "0") + "/" + 
            (ids.dom ? ids.dom : "0") + "/" + 
            hd.from.type + "/" + hd.from.id + "/" + 
            hd.to.type + "/" + hd.to.id + "/" + 
            hd.entry.type + "/" + hd.entry.id;
    return tpc;
}


