let dispatch = {
    getValidEdge : function() {
        let values = Object.values(data.edges);
        let edg;
        for(let i = 0; i < values.length; i++) {
            let value = values[i];
            if (value.enabled && value.online && value.online()) {
                edg = value;
                break;
            }
        }

        return edg;
    },

    dispatchDevice : function(devId){

        let dev = data.devices[devId];
        if (!dev) return;

        let edg = dev.edge;
        if (edg) edg = data.edges[edg.id];

        if (!edg || !edg.id || !edg.enabled || !(edg.online && edg.online()) ) {
            if (edg  && edg.devices)
                delete edg.devices[dev.id];
            edg = this.getValidEdge();
        }
        if (edg) {
            let devs = edg.devices || {};
            devs[dev.id] = dev;
            edg.devices = devs; 
        }

        dev.edge = edg;
        return edg;
    }
}



common.dispatch = dispatch;

return {payload: common};

