let keyToObjId = function(objs) {
    let keys = Object.keys(objs);
    keys.forEach(key => {
        let obj = objs[key];
        if (typeof obj == "object") 
            obj.id = key;
    })        
}

let keyDot2DUnder = function (objs, recu) {
    Object.keys(objs).forEach(key => {
        let obj = objs[key];
        delete objs[key];
        key = key.replace(/\./g, "__");
        objs[key] = obj;
        if (typeof obj == "object" && recu) 
            this.keyDot2DUnder(obj, recu);
    })     
}

let keyDUnder2Dot = function (objs, recu) {
    Object.keys(objs).forEach(key => {
        let obj = objs[key];
        delete objs[key];
        key = key.replace(/__/g, ".");
        objs[key] = obj;
        if (typeof obj == "object" && recu) 
            this.keyDUnder2Dot(obj, recu);
    })     
}


