let fixUpData = function() {
    let doms = data.app.domains;
    Object.keys(doms).forEach(domKey => {
        let dom = doms[domKey];
        dom.id = domKey;
        dom.app = data.app;
        let dsps = dom.dispatchers || {};
        dom.dispatchers = dsps;
        Object.keys(dsps).forEach(dspKey => {
            let dsp = dsps[dspKey];
            dsp.id = dspKey;
            dsp.domain = dom;

            data.dispatchers[dspKey] = dsp;
            let edgs = dsp.edges || {};
            dsp.edges = edgs;
            Object.keys(edgs).forEach(edgKey => {
                let edg = edgs[edgKey];
                edg.id = edgKey;
                edg.dispatcher = dsp;
                data.edges[edgKey] = edg;
                let devs = edg.devices || {};
                edg.devices = devs;
                Object.keys(devs).forEach(devKey => {
                    let dev = devs[devKey];
                    dev.id = devKey;
                    dev.edge = edg;
                    data.devices[devKey] = dev;
                })

            })

        })


    })
}

