let online = function(){
    let events = this.events;
    if (events && events.status)
        return events.status.pld.online;
    return 0;
}

let cachePayload = function(payload) {
    let hd = payload.hd;
    let pld = payload.pld;
    if (hd.from.type == "dev") {
        let dev = data.devices[hd.from.id] || {events:{}};
        dev.id = hd.from.id;
        if (hd.from.pid)
            dev.pid = hd.from.pid;
        dev.events[hd.entry.id] = payload;
        dev.online = online;
        data.devices[hd.from.id] = dev;
    }

    if (hd.from.type == "dsp" ) {
        if (hd.entry.type == "evt"){
            let dsp = data.disps[hd.from.id];
            dsp.events = dsp.events || {};
            dsp.events[hd.entry.id] = payload;
            dsp.online = online;
        }
    }
}

common.cachePayload = cachePayload;

return {payload: data};

