let getHDTopic = function(hd) {
    let tpc = "";
    if (!hd.from) hd.from = {};
    if (!hd.from.type) hd.from.type = "edg";
    if (!hd.from.id) hd.from.id = ids.edg;
    if (!hd.to.type) hd.to.type = "0";
    if (!hd.to.id) hd.to.id = "0";
    if (!hd.entry.type) hd.entry.type = "0";
    if (!hd.entry.id) hd.entry.id = "0";
    if (!hd.sid) hd.sid = "0";
    if (!hd.stp) hd.stp = 0;


    tpc =   ids.app + "/" + ids.dom + "/" + 
            hd.from.type + "/" + hd.from.id + "/" + 
            hd.to.type + "/" + hd.to.id + "/" + 
            hd.entry.type + "/" + hd.entry.id;
    return tpc;
}


common.getHDTopic = getHDTopic;

