let running = function() {
    return  this.z2m && this.z2m.status && this.z2m.status == "running" &&
            this.tcp && this.tcp.status && this.tcp.status == "listened"
}

let setDevice = (device, tcpPort) => {
    let dataDir = z2mDir + "/data/" + device.id; 
    let execStr = "cd " + z2mDir;
    if (os.platform == "win32"){
        execStr =execStr + "&& set ZIGBEE2MQTT_DATA=" + dataDir;
        execStr =execStr + "&& npm start";
    }

    let z2m = device.z2m || {};
    z2m.tcp = {
            port: tcpPort,
            status: null
        };
    z2m.z2m = {
            configuration: {
                mqtt: {
                    base_topic: device.id,
                    server: "mqtt://push-access.sdp.101.com:1780",
                    user: "ioe"
                },
                serial: {
                    port: "tcp://127.0.0.1:" + tcpPort
                },
                frontend: {
                    port: tcpPort - 100
                }
            },
            topics: {
                sub: common.expandTopic.expand(topics.dysubs.dev_evt_raw, device.id),
                pub: common.getHDTopic({to: {type: "dev", id: device.id}, 
                                        entry: {type: "svc", id: topics.pubs.dev_svc_raw}})
            },
            datadir: dataDir,
            configfile: "configuration.yaml",
            configfullfile: dataDir + "/configuration.yaml",
            databasefile: "database.db",
            databasefullfile: dataDir + "/database.db",
            exec: execStr,
            status: null,
            datafile: z2m.z2m && z2m.z2m.datafile
        };
    z2m.running = running;
    device.z2m = z2m;
}


let initZ3CoDevice = function(did) {
    let device = devices[did];
    if (device && (!device.z2m || !device.z2m.tcp || !device.z2m.z2m)) {
        setDevice(device, tcpPort++);
    }

    return device;
}


common.initZ3CoDevice = initZ3CoDevice;

