let fixUpDev = function(_dev, notNewIfNotExist) {
    if (!_dev || !_dev.id || !_dev.dom_id) {
        if (_dev && _dev.id)
            data.devices[_dev.id] = _dev;
        return;
    }

    let devId = _dev.id;
    let devs = data.devices;
    let dev = devs[devId];
    let dysub = false;


    if (!dev) {
        if (notNewIfNotExist)
            dev = _dev;
        else
            dev = Object.assign({}, _dev);
        dysub = true;
    } else {
        dev = Object.assign(dev, _dev);
    }

    dev.id = devId;
    data.devices[devId] = dev;

    //订阅设备上下线
    if (dysub)  node.send([{device: dev}, null]);

}




