let objToTslProps = function(obj) {
    let props = [];
    Object.keys(obj).forEach(key => {
        let prop = {
            identifier: key,
            value: obj[key]
        }
        props.push(prop)
    })
    return props;
}

let tslPropsToObj = function(props, _obj) {
    let obj = _obj || {}
    props.forEach(prop => {
        obj[prop.identifier] = prop.value
    })
    return obj;
}

